// --------------------------------------------

void mc_testbench::exec_hybrid( ac_int<32, false > x[1024], ac_int<32, false > m,  ac_int<32, false > twiddle[32],  ac_int<32, false > twiddle_h[32],  ac_int<20, true > revArr[32],  ac_int<32, false > tw[1024],  ac_int<32, false > tw_h[1024])
{
   #ifndef CCS_SCVERIFY_USE_CCS_BLOCK
   that->cpp_testbench_active.write(true);
   capture_IN(x, m, twiddle, twiddle_h, revArr, tw, tw_h);
   hybrid(x, m, twiddle, twiddle_h, revArr, tw, tw_h);
   // throttle ac_channel based on number of calls to chan::size() or chan::empty() or chan::nb_read() (but not chan::available()) 
   if (1) {
      int cnt=0;
      if (cnt) std::cout << "mc_testbench.cpp: CONTINUES @ " << sc_time_stamp() << std::endl;
      if (cnt) that->cpp_testbench_active.write(true);
   }
   capture_OUT(x, m, twiddle, twiddle_h, revArr, tw, tw_h);
   #else
   #endif
}
