// Process: SC_THREAD run
// Static sensitivity: 

void mc_testbench::run() {
   testbench::enable_idle_sync_mode = false;
   testbench::idle_sync_stable_cycles = 1;
   _channel_mismatch = false;
   testbench::i_sample_ignore = false;
   testbench::i_sample_skip = false;
   testbench::i_sample_skip_quiet = false;
   testbench::i_sample_skip_once = false;
   testbench::i_sample_skip_noerr = false;
   testbench::i_sample_array_comp_first = -1;
   testbench::i_sample_array_comp_last = -1;
   testbench::i_sample_wait_ctrl.clear();
   i_sample_capture_count = 0;
   i_sample_iteration_count = 0;
   testbench::b_ignore = false;
   testbench::b_skip = false;
   testbench::b_skip_quiet = false;
   testbench::b_skip_once = false;
   testbench::b_skip_noerr = false;
   testbench::b_array_comp_first = -1;
   testbench::b_array_comp_last = -1;
   testbench::b_wait_ctrl.clear();
   b_capture_count = 0;
   b_iteration_count = 0;
   b_access_ptr = 0;
   testbench::y_ignore = false;
   testbench::y_skip = false;
   testbench::y_skip_quiet = false;
   testbench::y_skip_once = false;
   testbench::y_skip_noerr = false;
   testbench::y_array_comp_first = -1;
   testbench::y_array_comp_last = -1;
   testbench::y_use_mask = false;
   testbench::y_output_mask = (ac_fixed<9, 1, true, AC_RND, AC_SAT_SYM >) ~0;
   testbench::y_wait_ctrl.clear();
   y_capture_count = 0;
   y_iteration_count = 0;
   testbench testbench_inst(sc_argc(), sc_argv());
   main_exit_code = testbench_inst.main();
   cout<<"Info: Execution of user-supplied C++ testbench 'main()' has completed with exit code = " << main_exit_code << endl;
   cout<<endl;
   cout<<"Info: Collecting data completed"<<endl;
   cout<<"   captured "<<i_sample_capture_count<<" values of i_sample"<<endl;
   cout<<"   captured "<<b_capture_count<<" values of b"<<endl;
   cout<<"   captured "<<y_capture_count<<" values of y"<<endl;
   testbench_ended = true;
   testbench_end_event.notify(SC_ZERO_TIME);
}
