void mc_testbench::run() {
   testbench::enable_idle_sync_mode = false;
   testbench::idle_sync_stable_cycles = 1;
   _channel_mismatch = false;
   testbench::base_ignore = false;
   testbench::base_skip = false;
   testbench::base_skip_quiet = false;
   testbench::base_skip_once = false;
   testbench::base_skip_noerr = false;
   testbench::base_array_comp_first = -1;
   testbench::base_array_comp_last = -1;
   testbench::base_wait_ctrl.clear();
   base_capture_count = 0;
   base_iteration_count = 0;
   testbench::exp_ignore = false;
   testbench::exp_skip = false;
   testbench::exp_skip_quiet = false;
   testbench::exp_skip_once = false;
   testbench::exp_skip_noerr = false;
   testbench::exp_array_comp_first = -1;
   testbench::exp_array_comp_last = -1;
   testbench::exp_wait_ctrl.clear();
   exp_capture_count = 0;
   exp_iteration_count = 0;
   testbench::m_ignore = false;
   testbench::m_skip = false;
   testbench::m_skip_quiet = false;
   testbench::m_skip_once = false;
   testbench::m_skip_noerr = false;
   testbench::m_array_comp_first = -1;
   testbench::m_array_comp_last = -1;
   testbench::m_wait_ctrl.clear();
   m_capture_count = 0;
   m_iteration_count = 0;
   testbench::result_ignore = false;
   testbench::result_skip = false;
   testbench::result_skip_quiet = false;
   testbench::result_skip_once = false;
   testbench::result_skip_noerr = false;
   testbench::result_array_comp_first = -1;
   testbench::result_array_comp_last = -1;
   testbench::result_IN_wait_ctrl.clear();
   result_IN_capture_count = 0;
   result_IN_iteration_count = 0;
   testbench::result_use_mask = false;
   testbench::result_output_mask = (ac_int<64, false >) ~0;
   testbench::result_wait_ctrl.clear();
   result_capture_count = 0;
   result_iteration_count = 0;
   testbench testbench_inst(sc_argc(), sc_argv());
   main_exit_code = testbench_inst.main();
   cout<<"Info: Execution of user-supplied C++ testbench 'main()' has completed with exit code = " << main_exit_code << endl;
   cout<<endl;
   cout<<"Info: Collecting data completed"<<endl;
   cout<<"   captured "<<base_capture_count<<" values of base"<<endl;
   cout<<"   captured "<<exp_capture_count<<" values of exp"<<endl;
   cout<<"   captured "<<m_capture_count<<" values of m"<<endl;
   cout<<"   captured "<<result_IN_capture_count<<" values of result_IN"<<endl;
   cout<<"   captured "<<result_capture_count<<" values of result"<<endl;
   testbench_ended = true;
   testbench_end_event.notify(SC_ZERO_TIME);
}
